"local Players = game:GetService(\"Players\")\r\nlocal localPlayer = Players.LocalPlayer\r\nlocal workspace = game:GetService(\"Workspace\")\r\n\r\nlocal screenGui = Instance.new(\"ScreenGui\")\r\nscreenGui.Parent = localPlayer:WaitForChild(\"PlayerGui\")\r\n\r\nlocal frame = Instance.new(\"Frame\")\r\nframe.Parent = screenGui\r\nframe.Size = UDim2.new(0.2, 0, 0.2, 0)\r\nframe.Position = UDim2.new(0.4, 0, 0.4, 0)\r\nframe.BackgroundTransparency = 0.5\r\nframe.Active = true\r\nframe.Draggable = true\r\n\r\nlocal minimizeButton = Instance.new(\"TextButton\")\r\nminimizeButton.Parent = frame\r\nminimizeButton.Size = UDim2.new(0.1, 0, 0.1, 0)\r\nminimizeButton.Position = UDim2.new(0.9, 0, 0, 0)\r\nminimizeButton.Text = \"_\"\r\n\r\nlocal minimized = false\r\n\r\nminimizeButton.MouseButton1Click:Connect(function()\r\n    if minimized then\r\n        frame.Size = UDim2.new(0.2, 0, 0.2, 0)\r\n        minimized = false\r\n    else\r\n        frame.Size = UDim2.new(0.2, 0, 0.1, 0)\r\n        minimized = true\r\n    end\r\nend)\r\n\r\nlocal numberInput = Instance.new(\"TextBox\")\r\nnumberInput.Parent = frame\r\nnumberInput.Position = UDim2.new(0.1, 0, 0.1, 0)\r\nnumberInput.Size = UDim2.new(0.8, 0, 0.2, 0)\r\nnumberInput.Text = \"Enter a number\"\r\nnumberInput.ClearTextOnFocus = true\r\n\r\nlocal generateButton = Instance.new(\"TextButton\")\r\ngenerateButton.Parent = frame\r\ngenerateButton.Position = UDim2.new(0.1, 0, 0.4, 0)\r\ngenerateButton.Size = UDim2.new(0.8, 0, 0.2, 0)\r\ngenerateButton.Text = \"Generate\"\r\n\r\ngenerateButton.MouseButton1Click:Connect(function()\r\n    local numBaseplates = tonumber(numberInput.Text)\r\n    \r\n    if numBaseplates then\r\n        local baseplate = workspace:FindFirstChild(\"Baseplate\")\r\n        \r\n        if baseplate then\r\n            local baseplateSize = baseplate.Size\r\n\r\n            for x = -numBaseplates, numBaseplates do\r\n                for z = -numBaseplates, numBaseplates do\r\n                    if x == 0 and z == 0 then\r\n                        continue\r\n                    end\r\n\r\n                    local newBaseplate = baseplate:Clone()\r\n                    newBaseplate.Parent = workspace\r\n                    newBaseplate.CFrame = baseplate.CFrame * CFrame.new(x * baseplateSize.X, 0, z * baseplateSize.Z)\r\n                end\r\n            end\r\n        else\r\n            warn(\"Baseplate not found in the workspace.\")\r\n        end\r\n    else\r\n        warn(\"Please enter a valid number.\")\r\n    end\r\nend)\r\n"